from typing import Dict, List, Tuple, Optional, Any, Union


# Generated by Aurora-X v3 (offline, safe)
def check_palindrome(s: str) -> bool:
    """Return True if s is a palindrome (unicode-safe)."""
    s_norm = s
    return s_norm == s_norm[::-1]
