def format_mmss(ms: int) -> str:
    """
    Convert milliseconds to MM:SS format

    Args:
        ms: Time in milliseconds

    Returns:
        str: Formatted time as MM:SS

    Examples:
        >>> format_mmss(0)
        '00:00'
        >>> format_mmss(1000)
        '00:01'
        >>> format_mmss(60000)
        '01:00'
        >>> format_mmss(125000)
        '02:05'
