                <input type="number" id="seconds" min="0" max="59" value="0">
            </div>
        </div>

        <div class="controls">
            <button class="btn btn-start" id="startBtn">Start</button>
            <button class="btn btn-pause" id="pauseBtn" disabled>Pause</button>
